import { useNavigate } from 'react-router-dom';
import { ArrowRight } from 'lucide-react';
import { motion } from 'framer-motion';
import Header from '@/components/layout/Header';
import { Button } from '@/components/ui/button';
import { useTryOnStore } from '@/store/useTryOnStore';
import PhotoDropZone from '@/components/upload/PhotoDropZone';
import PhotoPreview from '@/components/upload/PhotoPreview';
import UploadTips from '@/components/upload/UploadTips';

export default function UploadPage() {
  const navigate = useNavigate();
  const { uploadedPhoto } = useTryOnStore();

  return (
    <div className="min-h-screen flex flex-col bg-background">
      <Header />
      <main className="flex-1 section-padding">
        <div className="container-app">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="mb-10 text-center"
          >
            <h1 className="text-3xl font-semibold tracking-tight md:text-4xl">
              上传你的照片
            </h1>
            <p className="mt-3 text-lg text-muted-foreground">
              选择一张全身照，AI 将为你生成换装效果
            </p>
          </motion.div>

          <div className="mx-auto grid max-w-4xl gap-8 lg:grid-cols-[1fr_280px]">
            <div>
              {uploadedPhoto ? <PhotoPreview /> : <PhotoDropZone />}

              {uploadedPhoto && (
                <motion.div
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="mt-8 flex justify-center"
                >
                  <Button
                    variant="hero"
                    size="xl"
                    onClick={() => navigate('/catalog')}
                  >
                    选择服装
                    <ArrowRight className="ml-2 h-5 w-5" />
                  </Button>
                </motion.div>
              )}
            </div>

            <div className="hidden lg:block">
              <UploadTips />
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

import { useNavigate } from 'react-router-dom';
import { useEffect } from 'react';
import { ArrowRight } from 'lucide-react';
import { motion } from 'framer-motion';
import Header from '@/components/layout/Header';
import { Button } from '@/components/ui/button';
import { useTryOnStore } from '@/store/useTryOnStore';
import ClothingGrid from '@/components/catalog/ClothingGrid';
import { AspectRatio } from '@/components/ui/aspect-ratio';

export default function CatalogPage() {
  const navigate = useNavigate();
  const { uploadedPhoto, selectedGarment } = useTryOnStore();

  useEffect(() => {
    if (!uploadedPhoto) {
      navigate('/upload');
    }
  }, [uploadedPhoto, navigate]);

  if (!uploadedPhoto) return null;

  return (
    <div className="min-h-screen flex flex-col bg-background">
      <Header />
      <main className="flex-1 section-padding">
        <div className="container-app">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="mb-10 text-center"
          >
            <h1 className="text-3xl font-semibold tracking-tight md:text-4xl">
              选择你心仪的服装
            </h1>
            <p className="mt-3 text-lg text-muted-foreground">
              浏览服装库，点击选择想要试穿的单品
            </p>
          </motion.div>

          <div className="grid gap-8 lg:grid-cols-[200px_1fr]">
            {/* Person photo sidebar */}
            <div className="hidden lg:block">
              <div className="sticky top-24">
                <span className="mb-2 block text-xs font-medium uppercase tracking-widest text-muted-foreground">
                  你的照片
                </span>
                <div className="overflow-hidden rounded-lg border border-border">
                  <AspectRatio ratio={3 / 4}>
                    <img
                      src={uploadedPhoto.previewUrl}
                      alt="你的照片"
                      className="h-full w-full object-cover"
                    />
                  </AspectRatio>
                </div>
              </div>
            </div>

            {/* Clothing grid */}
            <div>
              <ClothingGrid />
            </div>
          </div>

          {/* Fixed bottom CTA */}
          {selectedGarment && (
            <motion.div
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              className="fixed bottom-0 left-0 right-0 z-40 border-t border-border bg-background/90 backdrop-blur-md p-4"
            >
              <div className="container-app flex items-center justify-between">
                <div className="flex items-center gap-3">
                  <div className="h-12 w-10 overflow-hidden rounded-md border border-border">
                    <img
                      src={selectedGarment.thumbnailUrl}
                      alt={selectedGarment.name}
                      className="h-full w-full object-cover"
                    />
                  </div>
                  <div>
                    <p className="text-sm font-medium text-foreground">{selectedGarment.name}</p>
                    <p className="text-xs text-muted-foreground">{selectedGarment.brand}</p>
                  </div>
                </div>
                <Button
                  variant="hero"
                  onClick={() => navigate('/tryon')}
                >
                  开始试穿
                  <ArrowRight className="ml-2 h-4 w-4" />
                </Button>
              </div>
            </motion.div>
          )}
        </div>
      </main>
    </div>
  );
}

import { useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { toast } from 'sonner';
import Header from '@/components/layout/Header';
import { useTryOnStore } from '@/store/useTryOnStore';
import PersonPanel from '@/components/tryon/PersonPanel';
import GarmentPanel from '@/components/tryon/GarmentPanel';
import GenerateButton from '@/components/tryon/GenerateButton';
import LoadingOverlay from '@/components/tryon/LoadingOverlay';
import ResultView from '@/components/tryon/ResultView';
import ResultActions from '@/components/tryon/ResultActions';
import { generateTryOn } from '@/data/mockApi';

export default function TryOnPage() {
  const navigate = useNavigate();
  const {
    uploadedPhoto,
    selectedGarment,
    isGenerating,
    generationProgress,
    currentResult,
    setIsGenerating,
    setGenerationProgress,
    setCurrentResult,
    addToHistory,
  } = useTryOnStore();

  useEffect(() => {
    if (!uploadedPhoto || !selectedGarment) {
      navigate(uploadedPhoto ? '/catalog' : '/upload');
    }
  }, [uploadedPhoto, selectedGarment, navigate]);

  const handleGenerate = useCallback(async () => {
    if (!uploadedPhoto || !selectedGarment) return;

    setIsGenerating(true);
    setGenerationProgress(0);

    try {
      const result = await generateTryOn(
        uploadedPhoto.previewUrl,
        selectedGarment.id,
        selectedGarment.imageUrl,
        (progress) => setGenerationProgress(progress)
      );

      setCurrentResult(result);
      addToHistory(result);
      toast.success('换装效果已生成');
    } catch {
      toast.error('生成失败，请重试');
    } finally {
      setIsGenerating(false);
      setGenerationProgress(0);
    }
  }, [uploadedPhoto, selectedGarment, setIsGenerating, setGenerationProgress, setCurrentResult, addToHistory]);

  if (!uploadedPhoto || !selectedGarment) return null;

  return (
    <div className="min-h-screen flex flex-col bg-background">
      <Header />
      <main className="flex-1 section-padding">
        <div className="container-app max-w-4xl">
          {currentResult ? (
            <>
              <ResultView result={currentResult} />
              <div className="mt-10">
                <ResultActions result={currentResult} />
              </div>
            </>
          ) : (
            <>
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="mb-10 text-center"
              >
                <h1 className="text-3xl font-semibold tracking-tight md:text-4xl">
                  确认试穿
                </h1>
                <p className="mt-3 text-lg text-muted-foreground">
                  确认照片和服装后，点击生成按钮
                </p>
              </motion.div>

              <div className="grid gap-8 md:grid-cols-2">
                <motion.div
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 0.1 }}
                >
                  <PersonPanel photoUrl={uploadedPhoto.previewUrl} />
                </motion.div>
                <motion.div
                  initial={{ opacity: 0, x: 20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 0.2 }}
                >
                  <GarmentPanel garment={selectedGarment} />
                </motion.div>
              </div>

              <motion.div
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.3 }}
                className="mt-10 text-center"
              >
                <GenerateButton onClick={handleGenerate} disabled={isGenerating} />
              </motion.div>
            </>
          )}
        </div>
      </main>

      <LoadingOverlay isVisible={isGenerating} progress={generationProgress} />
    </div>
  );
}

import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { Toaster } from "@/components/ui/sonner";
import Index from "./pages/Index";
import UploadPage from "./pages/UploadPage";
import CatalogPage from "./pages/CatalogPage";
import TryOnPage from "./pages/TryOnPage";
import NotFound from "./pages/NotFound";

const queryClient = new QueryClient();

const App = () => (
  <QueryClientProvider client={queryClient}>
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Index />} />
        <Route path="/upload" element={<UploadPage />} />
        <Route path="/catalog" element={<CatalogPage />} />
        <Route path="/tryon" element={<TryOnPage />} />
        <Route path="*" element={<NotFound />} />
      </Routes>
    </BrowserRouter>
    <Toaster />
  </QueryClientProvider>
);

export default App;

