import { TryOnResult } from '@/types';
import { v4 as uuidv4 } from 'uuid';

// Simulate AI generation with a progress callback
export async function generateTryOn(
  personPhotoUrl: string,
  garmentId: string,
  garmentImageUrl: string,
  onProgress: (progress: number) => void
): Promise<TryOnResult> {
  // Simulate 3-5 second generation time with progress updates
  const totalDuration = 3000 + Math.random() * 2000;
  const steps = 20;
  const stepDuration = totalDuration / steps;

  for (let i = 1; i <= steps; i++) {
    await new Promise((resolve) => setTimeout(resolve, stepDuration));
    onProgress(Math.min((i / steps) * 100, 100));
  }

  // For the mock, return the person photo as the "result"
  // When real API is integrated, this will return the AI-generated image
  return {
    id: uuidv4(),
    personPhotoUrl,
    garmentId,
    garmentImageUrl,
    resultImageUrl: personPhotoUrl, // Placeholder -- will be replaced by real API
    generatedAt: new Date(),
  };
}
```

